import React from "react";

export default class Filters extends React.Component {
  render() {
    const { onChangePage, page } = this.props;
    return (
      <div className="btn-group">
        <button
          type="button"
          className="btn btn-secondary"
          disabled={page === 1}
          onClick={onChangePage.bind(null, page - 1)}
        >
          Назад
        </button>
        <button
          type="button"
          className="btn btn-secondary"
          onClick={onChangePage.bind(null, page + 1)}
        >
          Вперед
        </button>
      </div>
    );
  }
}
